import React, { useState, useEffect } from "react";
import axios from "axios";
import Form from "react-bootstrap/Form";
import ListGroup from "react-bootstrap/ListGroup";
import "../../styles/gamePage.css";

function GameSearch() {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [showDropdown, setShowDropdown] = useState(false); // Track whether to show the dropdown

  useEffect(() => {
    // Fetch game suggestions when the search term changes
    async function fetchGameSuggestions() {
      try {
        if (searchTerm.trim() === "") {
          setSearchResults([]); // Clear results if the search term is empty
          return;
        }

        const response = await axios.get(
          "https://api.rawg.io/api/games",
          {
            params: {
              key: process.env.REACT_APP_RAWG_API_KEY,
              search: searchTerm,
              page_size: 10, // Limit the number of results
            },
          }
        );

        setSearchResults(response.data.results);
        setShowDropdown(true); // Show the dropdown when results are available
      } catch (error) {
        console.error("Error fetching game suggestions", error);
      }
    }

    fetchGameSuggestions();
  }, [searchTerm]);

  const handleItemClick = (clickedItem) => {
    setSearchTerm(clickedItem.name); // Set the input value to the clicked item's name
    setSearchResults([]); // Clear the dropdown
    setShowDropdown(false); // Hide the dropdown when an item is clicked
  };

  return (
    <div className="game-search">
      <h2>Game Search</h2>
      <Form>
        <Form.Group controlId="searchTerm">
          <Form.Control
            type="text"
            placeholder="Search for a game"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            onFocus={() => setShowDropdown(true)} // Show the dropdown when the input is focused
            onBlur={() => setShowDropdown(false)} // Hide the dropdown when the input loses focus
          />
        </Form.Group>
      </Form>
      {showDropdown && (
        <ListGroup>
          {searchResults.map((game) => (
            <ListGroup.Item
              key={game.id}
              onClick={() => handleItemClick(game)}
            >
              {game.name}
            </ListGroup.Item>
          ))}
        </ListGroup>
      )}
    </div>
  );
}

export default GameSearch;
