import React from 'react';
import { useParams } from 'react-router-dom';
import { useQuery } from '@apollo/client';
import Auth from '../utils/auth';
import { QUERY_USER, QUERY_ME } from '../utils/queries';
import Card from 'react-bootstrap/Card';

function Profile() {
  const { username: userParam } = useParams();
  const { loading, data } = useQuery(userParam === 'me' ? QUERY_ME : QUERY_USER, {
    variables: { username: userParam === 'me' ? undefined : userParam },
  });
  const user = data?.me || data?.user || {};

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <Card className="m-5">
      <Card.Body className="p-5">
        {Auth.loggedIn() ? (
          <p>Welcome to your profile page, {user.username}!</p>
        ) : (
          <p>You need to be logged in to see this. Use the navigation links above to sign up or log in!</p>
        )}
      </Card.Body>
    </Card>
  );
}

export default Profile;
