import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import Form from "react-bootstrap/Form";
import ListGroup from "react-bootstrap/ListGroup";
import "../../styles/gamePage.css";

function GameSearch() {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [showDropdown, setShowDropdown] = useState(false);
  const searchInputRef = useRef(null);

  // Add an event listener to detect clicks outside of the component
  useEffect(() => {
    function handleClickOutside(event) {
      if (searchInputRef.current && !searchInputRef.current.contains(event.target)) {
        // Clicked outside the input field, close the dropdown
        setShowDropdown(false);
      }
    }

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  useEffect(() => {
    // Fetch game suggestions when the search term changes
    async function fetchGameSuggestions() {
      try {
        if (searchTerm.trim() === "") {
          setSearchResults([]); // Clear results if the search term is empty
          return;
        }

        const response = await axios.get(
          "https://api.rawg.io/api/games",
          {
            params: {
              key: process.env.REACT_APP_RAWG_API_KEY,
              search: searchTerm,
              page_size: 10, // Limit the number of results
            },
          }
        );

        setSearchResults(response.data.results);
        setShowDropdown(true);
      } catch (error) {
        console.error("Error fetching game suggestions", error);
      }
    }

    fetchGameSuggestions();
  }, [searchTerm]);

  const handleItemClick = (clickedItem) => {
    setSearchTerm(clickedItem.name); // Set the input value to the clicked item's name
    setSearchResults([]); // Clear the dropdown
  };

  return (
    <div className="game-search">
      <h2>Game Search</h2>
      <Form>
        <Form.Group controlId="searchTerm">
          <Form.Control
            type="text"
            placeholder="Search for a game"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            // onFocus={() => setShowDropdown(true)}
            // ref={searchInputRef}
          />
        </Form.Group>
      </Form>
      {showDropdown && (
        <div className="search-dropdown">
          <ListGroup>
            {searchResults.map((game) => (
              <ListGroup.Item key={game.id} onClick={() => handleItemClick(game)}>
                {game.name}
              </ListGroup.Item>
            ))}
          </ListGroup>
        </div>
      )}
    </div>
  );
}

export default GameSearch;
