import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  createHttpLink,
} from "@apollo/client";
import { setContext } from "@apollo/client/link/context";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import NavBar from "./components/NavBar";
import Home from "./components/pages/Home";
import Contact from "./components/pages/Contact";
import About from "./components/pages/About";
import ProfilePost from "./components/pages/PostForm";
import Login from "./components/pages/Login";
import Signup from "./components/pages/Signup";
import Profile from "./components/pages/Profile";
import Footer from "./components/Footer";
import Header from "./components/Header";
import Sidebar from "./components/Sidebar";
import Gamepage from "./components/pages/GamePage";
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import './theme.scss'

const httpLink = createHttpLink({
  uri: "/graphql",
});

const authLink = setContext((_, { headers }) => {
  const token = localStorage.getItem("id_token");
  console.log(token);
  return {
    headers: {
      ...headers,
      authorization: token ? `Bearer ${token}` : "",
    },
  };
});

const client = new ApolloClient({
  link: authLink.concat(httpLink),
  cache: new InMemoryCache(),
});

const incrementApiCallCount = () => {
  const currentCount = parseInt(localStorage.getItem("apiCallCount")) || 0;
  const newCount = currentCount + 1;
  localStorage.setItem("apiCallCount", newCount);
};

function App() {


  return (
    <ApolloProvider client={client}>
      <Router>
        <div className="app-container">
          <Header />

          <NavBar className="navbar" />
          <div className="content-container">
            <div className="sidebar">
              <Sidebar />
            </div>
            <div className="main-content">
              <Routes>
                <Route path="/home" element={<Home />} />
                <Route path="/login" element={<Login />} />
                <Route path="/signup" element={<Signup />} />
                <Route path="/gamepage" element={<Gamepage />} />
                <Route path="/profile" element={<Profile />} />
                {/* View someone else's profile */}
                <Route path="/profile/:username" element={<Profile />} />
                {/* View the user's own profile */}
                <Route path="/profile/me" element={<Profile />} />
                <Route path="/contact" element={<Contact />} />
                <Route path="/about" element={<About />} />
                <Route path="/post" element={<ProfilePost />} />
              </Routes>
              </div>
          </div>
          <Footer />
        </div>
      </Router>
    </ApolloProvider>
  );
}
export default App;
