import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import Form from "react-bootstrap/Form";
import ListGroup from "react-bootstrap/ListGroup";
import "../../styles/gamePage.css";

function GameSearch() {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [isDropdownVisible, setIsDropdownVisible] = useState(false);
  const searchInputRef = useRef(null);

  useEffect(() => {
    // Fetch game suggestions when the search term changes
    async function fetchGameSuggestions() {
      try {
        if (searchTerm.trim() === "") {
          setSearchResults([]); // Clear results if the search term is empty
          return;
        }

        const response = await axios.get(
          "https://api.rawg.io/api/games",
          {
            params: {
              key: process.env.REACT_APP_RAWG_API_KEY,
              search: searchTerm,
              page_size: 10, // Limit the number of results
            },
          }
        );

        setSearchResults(response.data.results);
        setIsDropdownVisible(true); // Show the dropdown when results are available
      } catch (error) {
        console.error("Error fetching game suggestions", error);
      }
    }

    fetchGameSuggestions();
  }, [searchTerm]);

  const handleItemClick = (clickedItem) => {
    setSearchTerm(clickedItem.name); // Set the input value to the clicked item's name
    setIsDropdownVisible(false); // Hide the dropdown
    searchInputRef.current.blur(); // Lose focus on the input
  };

  return (
    <div className="game-search">
      <h2>Game Search</h2>
      <Form>
        <Form.Group controlId="searchTerm">
          <Form.Control
            type="text"
            placeholder="Search for a game"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            onFocus={() => setIsDropdownVisible(true)} // Show the dropdown when the input is focused
            onBlur={() => {
              // Hide the dropdown and lose focus after a brief delay
              setTimeout(() => {
                setIsDropdownVisible(false);
                searchInputRef.current.blur();
              }, 100000);
            }}
            ref={searchInputRef}
          />
        </Form.Group>
      </Form>
      {isDropdownVisible && (
        <ListGroup>
          {searchResults.map((game) => (
            <ListGroup.Item
              key={game.id}
              onClick={() => handleItemClick(game)}
            >
              {game.name}
            </ListGroup.Item>
          ))}
        </ListGroup>
      )}
    </div>
  );
}

export default GameSearch;
